﻿import { expect } from 'chai';
import {ValidationProvider} from './validation-provider';
import { JSDOM } from 'jsdom';

const dom = new JSDOM(`<!DOCTYPE html><html lang="en"><body></body></html>`);
const document = dom.window.document;

function generateInputGroup(inputId: string, inputType: string, validationType: string, value?: any): HTMLDivElement {
    const group = document.createElement('div');
    group.classList.add('form-group');
    const errorMessage = document.createElement('span');
    errorMessage.setAttribute('data-valmsg-for', inputId);

    const textInput = document.createElement('input');
    textInput.type = inputType;
    textInput.name = inputId;
    textInput.id = inputId;
    textInput.dataset[validationType] = 'some-message';
    if(value)
        textInput.value = value;
    group.appendChild(textInput);
    group.appendChild(errorMessage);

    return group;
}

describe('Validation Helper', () => {
    describe('validate', () => {
        it('when form has validate then result is false', () => {

            const form = document.createElement('form');
            form.dataset.noValidate = '';
            form.appendChild(generateInputGroup('input1', 'text', 'valRequired'));
            const validator: ValidationProvider = new ValidationProvider(form);
            expect(validator.validate().isValid).to.be.true;
        });
        it('when form has validatable elements and elements are valid then result is true', () => {
            const form = document.createElement('form');
            form.appendChild(generateInputGroup('input1', 'text', 'valRequired', 'value'));
            const validator: ValidationProvider = new ValidationProvider(form);
            expect(validator.validate().isValid).to.be.true;
        });
        it('when form has validatable elements and elements are not valid then result is false', () => {
            const form = document.createElement('form');
            form.appendChild(generateInputGroup('input1', 'text', 'valRequired'));
            const validator: ValidationProvider = new ValidationProvider(form);
            expect(validator.validate().isValid).to.be.false;
        });
        it('when form has validatable elements and elements are not valid then element should be decorated', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valRequired');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
            expect(group.querySelector('span[data-valmsg-for]').innerHTML).to.eq('some-message')
        });
        it('when form has validatable elements and elements are valid then element should be decorated', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valRequired', 'val');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-success')).to.be.true;
            expect(group.querySelector('span[data-valmsg-for]').innerHTML).to.eq('')
        });
    });
    describe('reset', () => {
        it('when form has been validate expect validation to be removed', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valRequired');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            validator.reset();
            expect(group.classList.contains('has-error')).to.be.false;
            expect(group.classList.contains('has-success')).to.be.false;
            expect(group.querySelector('span[data-valmsg-for]').innerHTML).to.eq('')
        });
    });
    describe('validation rules', () => {
        it('when valRequired expect a required validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valRequired');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
        it('when valEmail expect an email validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valEmail', 'value');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
        it('when valEmail expect an email validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valEmail', 'value');
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
        it('when valMinlength expect a length validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valMinlength', 'value');
            group.querySelector('input').dataset.valMinlengthMin = '6';
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
        it('when valEqualto expect a equality validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valEqualto', 'value');
            group.querySelector('input').dataset.valEqualtoOther = 'compare';
            const textInput = document.createElement('input');
            textInput.type = 'text';
            textInput.name = 'compare';
            textInput.id = 'compare';
            textInput.value = 'val';
            form.appendChild(group);
            form.appendChild(textInput);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
        it('when valRegex expect a format validation to be used', () => {
            const form = document.createElement('form');
            const group = generateInputGroup('input1', 'text', 'valRegex', 'value');
            group.querySelector('input').dataset.valRegexPattern = '^[0-9]$';
            form.appendChild(group);
            const validator: ValidationProvider = new ValidationProvider(form);
            validator.validate();
            expect(group.classList.contains('has-error')).to.be.true;
        });
    });
});

import validate from 'validate.js';

interface IValidationResult {
    isValid: boolean;
}

export class ValidationProvider {
    private readonly form: HTMLFormElement;
    private formSubmitted:boolean;
    private readonly elements: Element[];
    private readonly constraints: any;
    private hasValidated: boolean = false;
    private canBeValidated: boolean = false;

    constructor(formQuery: string | HTMLFormElement, validateOnSubmit: boolean = true) {
        const contextThis = this;
        if (formQuery.isPrototypeOf(String)) {
            this.form = document.querySelector(formQuery as string) as HTMLFormElement;
        } else {
            this.form = formQuery as HTMLFormElement;
        }
        if (!this.form)
            return;

        if (this.form.dataset.noValidate || this.form.dataset.noValidate === '')
            return;

        this.constraints = {};
        this.elements = [];
        const els = this.form.querySelectorAll('input:not([type="hidden"]):not([data-val="false"]), textarea:not([data-val="false"])');
        Array.prototype.forEach.call(els,
            (element: HTMLInputElement) => {
                let needsValidation: boolean = false;
                let elementId = element.id;
                if (elementId) {
                    elementId = elementId.replace('.', '_');
                    contextThis.constraints[elementId] = {};
                for (const i in element.dataset) {
                    if (element.dataset.hasOwnProperty(i)) {
                        switch (i) {
                            case 'valRequired':
                                needsValidation = true;
                                contextThis.constraints[elementId].presence = {
                                    message: `^${element.dataset[i]}`
                                };
                                break;
                            case 'valEmail':
                                needsValidation = true;
                                contextThis.constraints[elementId].email = {
                                    message: `^${element.dataset[i]}`
                                };
                                break;
                            case 'valMinlength':
                                needsValidation = true;
                                contextThis.constraints[elementId].length = {
                                    tooShort: `^${element.dataset[i]}`,
                                    minimum: parseInt(element.dataset.valMinlengthMin, 10)
                                };
                                break;
                            case 'valEqualto':
                                needsValidation = true;
                                if (element.dataset.valEqualtoOther.charAt(0) === '*') {
                                    const searchTerm = element.dataset.valEqualtoOther.replace('*.', '.');
                                    element.dataset.valEqualtoOther = document.querySelector('[name*="' + searchTerm + '"]').id
                                }
                                contextThis.constraints[elementId].equality = {
                                    message: `^${element.dataset[i]}`,
                                    attribute: element.dataset.valEqualtoOther,
                                    comparator: (v1: any, v2: any) => {
                                        return JSON.stringify(v1) === JSON.stringify(v2);
                                    }
                                };
                                break;
                            case 'valRegex':
                                needsValidation = true;
                                contextThis.constraints[elementId].format = {
                                    message: `^${element.dataset[i]}`,
                                    pattern: element.dataset.valRegexPattern,
                                    flags: 'i'
                                };
                                break;
                        }
                    }

                }
                if (needsValidation) {
                    this.canBeValidated = true;
                    element.addEventListener('blur', () => { contextThis.elementChange(); });
                    element.addEventListener('change', () => { contextThis.elementChange(); });
                    this.elements.push(element);
                }
            }
        });
        if(validateOnSubmit) {
            this.form.addEventListener('submit', (e) => { contextThis.formSubmit(e); });
        }
        this.formSubmitted = false;
    }

    public validate(): IValidationResult {
        if (!this.canBeValidated) {
            return {
                isValid: true
            };
        }
        this.hasValidated = true;
        return {
            isValid: !this.performValidation()
        };
    }

    public reset(): void {
        this.elements.forEach((element: HTMLInputElement) => {
            const group = element.closest('.form-group');
            group.classList.remove('has-success');
            group.classList.remove('has-error');
            const helpBlock = group.querySelector(`span[data-valmsg-for]`);
            helpBlock.innerHTML = '';
        });
    }

    private elementChange(): void {
        if (this.hasValidated) {
            this.performValidation();
        }
    }

    private formSubmit(event: Event): void {
        if (!this.canBeValidated)
            return;

        this.hasValidated = true;

        if (this.performValidation()) {
            event.preventDefault();
            return;
        }
        if (this.formSubmitted) {
            event.preventDefault();
        }
        this.formSubmitted = true;
    }

    private performValidation(): any {
        const formValues = JSON.parse(JSON.stringify(validate.collectFormValues(this.form)).replace(/\\\\\\\\\./g, '_'));
        const validationResult = validate(formValues, this.constraints);
            this.elements.forEach((element: HTMLInputElement) => {
                ValidationProvider.decorateElement(element, validationResult);
        });
        return validationResult;
    }

    private static decorateElement(element: HTMLInputElement, validationResult: any) {
        const group = element.closest('.form-group');
        group.classList.remove('has-success');
        group.classList.remove('has-error');
        let elementId = element.id;
        if (elementId) {
            elementId = elementId.replace('.', '_');
            const helpblock = group.querySelector(`span[data-valmsg-for]`);
            helpblock.innerHTML = '';
            if (validationResult) {
                const item = validationResult[elementId];
                if (item) {
                    group.classList.add('has-error');
                    helpblock.innerHTML = item[0];
                } else {
                    group.classList.add('has-success');
                }
            } else {
                group.classList.add('has-success');
            }
        }
    }
}
